
public class ClassMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Calcular Raiz Quadrada 900 = "+ Math.sqrt(900));
		double c = 13.0, d = 3.0, f = 4.0;
		System.out.println("Calcular Raiz Quadrada 13.0 + 3.0 * 4.0 =  "+ Math.sqrt(c + d * f));
		System.out.println("Cosseno de 0.0 = "+ Math.cos(0.0));
		System.out.println("Exponencial e¹ de 1.0 = "+ Math.exp(1.0));
		System.out.println("logaritmo natural de x (base e) = " + Math.log(Math.E));
		System.out.println("maior valor de x e y = " + Math.max(2.3, 12.7));
		
	}

}
